INSERT INTO `wp_postmeta` VALUES (1,3,'blogger_blog','blog.diybio.org'),(2,3,'blogger_author','Jason Bobehttp://www.blogger.com/profile/17930208291961247317noreply@blogger.com'),(3,3,'blogger_permalink','/2008/05/dont-phage-me-bro.html'),(4,4,'blogger_blog','blog.diybio.org'),(5,4,'blogger_author','Mackenziehttp://www.blogger.com/profile/17506983539612139522noreply@blogger.com'),(6,4,'blogger_permalink','/feeds/posts/default/3367022967326571301'),(7,5,'blogger_blog','blog.diybio.org'),(8,5,'blogger_author','Mackenziehttp://www.blogger.com/profile/17506983539612139522noreply@blogger.com'),(9,5,'blogger_permalink','/2008/07/diybio-in-5-minutes-oreilly-ignite.html'),(10,6,'blogger_blog','blog.diybio.org'),(11,6,'blogger_author','Mackenziehttp://www.blogger.com/profile/17506983539612139522noreply@blogger.com'),(12,6,'blogger_permalink','/2008/07/diybio-3-gel-electrophoresis.html'),(13,7,'blogger_blog','blog.diybio.org'),(14,7,'blogger_author','Jason Bobehttp://www.blogger.com/profile/17930208291961247317noreply@blogger.com'),(15,7,'blogger_permalink','/2008/07/environmentally-benign-diybio.html'),(16,8,'blogger_blog','blog.diybio.org'),(17,8,'blogger_author','Jason Bobehttp://www.blogger.com/profile/17930208291961247317noreply@blogger.com'),(18,8,'blogger_permalink','/2008/08/science-without-scientists.html'),(19,9,'blogger_blog','blog.diybio.org'),(20,9,'blogger_author','Jason Morrisonhttp://www.blogger.com/profile/01407368069531579473noreply@blogger.com'),(21,9,'blogger_permalink','/2008/10/diybio-meetup-in-boston-on-thursday.html'),(22,10,'blogger_blog','blog.diybio.org'),(23,10,'blogger_author','Jason Morrisonhttp://www.blogger.com/profile/01407368069531579473noreply@blogger.com'),(24,10,'blogger_permalink','/feeds/posts/default/8839478732873751705'),(25,11,'_edit_lock','1243656089'),(26,11,'_edit_last','1'),(27,15,'_edit_lock','1243656109'),(28,15,'_edit_last','1'),(29,17,'_edit_lock','1243656104'),(30,17,'_edit_last','1'),(31,21,'_edit_lock','1243655874'),(32,21,'